type PriceServer struct {
	chainLinkConfig      []entity.ChainLinkConfig
	uniswapV2PriceConfig []entity.UniswapV2PriceConfig
	node                 *NodeServer
	stop                 chan struct{}
}

func GetPriceServer(node *NodeServer) *PriceServer {
	return &PriceServer{
		chainLinkConfig:      make([]entity.ChainLinkConfig, 0),
		uniswapV2PriceConfig: make([]entity.UniswapV2PriceConfig, 0),
		node:                 node,
		stop:                 make(chan struct{}),
	}
}

func (p *PriceServer) Stop() {
	p.stop <- struct{}{}
}

func (p *PriceServer) Restart() {
	p.Stop()
	p.chainLinkConfig = []entity.ChainLinkConfig{}
	p.uniswapV2PriceConfig = []entity.UniswapV2PriceConfig{}
	p.Start()
}

func (p *PriceServer) Start() {
	// 查询配置
	if len(p.chainLinkConfig) == 0 {
		p.chainLinkConfig = entity.ListChainLinkConfig()
		p.uniswapV2PriceConfig = entity.ListUniswapPriceConfig()
	}

	// 获取chainLink价格
	for _, config := range p.chainLinkConfig {
		go getChainLinkPrice(p.node.Client, p.node.ChainId, config, p.stop)
	}
}

func fraction(numerator *big.Int, denominator *big.Int) *big.Int {
	result := new(big.Int)

	big1 := new(big.Int)
	big1.SetInt64(1)

	big144 := new(big.Int)
	big144.SetInt64(int64(math.Pow(2, 144))).Sub(big144, big1)

	big112 := new(big.Int)
	big112.SetInt64(int64(math.Pow(2, 112)))

	if numerator.Cmp(big144) <= 0 {
		result = numerator.Mul(numerator, big144).Div(numerator, denominator)
	} else {
		result = numerator.Mul(numerator, big112).Div(numerator, denominator)
	}
	return result
}

